import React, { Component } from 'react';
import './App.css';
// import listImage from './components/listImage';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';

class App extends Component {

    //constructor
    constructor(props){
        super(props);
        this.state = {
            imgs:[],
            isLoad: false,
        };
    }

    //overwrite componentDidMount for fetching API
    componentDidMount() {
        axios.get('https://api.unsplash.com/photos?per_page=25&client_id=83b9dc155a99bbcfbd0befebfb05a5ebffa221dce0381fbfd8070e3054fd722e')
            .then
            (response => {
            this.setState({ imgs:response.data,isLoad:true});
        })
        //     (function(response){
        //         this.setState({imgs:response.data,isLoad:true});
        // })
            .catch(function (error) {
                if (error.response) {
                    // The request was made and the server responded with a status code
                    // that falls out of the range of 2xx
                    console.log(error.response.data);
                    console.log(error.response.status);
                    console.log(error.response.headers);
                } else if (error.request) {
                    // The request was made but no response was received
                    // `error.request` is an instance of XMLHttpRequest in the browser and an instance of
                    // http.ClientRequest in node.js
                    console.log(error.request);
                } else {
                    // Something happened in setting up the request that triggered an Error
                    console.log('Error', error.message);
                }
                console.log(error.config);
            })
    }

    //render function
  render() {
    return (
      <div className="App">
          <div className="header">
              <div className="inner">
                  <h1 className="title">SearchBar</h1>
              </div>
          </div>
          <div className="content">
              {this.state.imgs.map((image) => (
                  <div class="card" style="width: 18rem;" key={image.id}>
                      <img class="card-img-top" src={image.urls.regular} alt=""></img>
                  <div class="card-body">
                  <p class="card-text">Photo by:{image.user.name}</p>
                  </div>
                  </div>

              ))}
          </div>
      </div>
    );
  }
}

export default App;
