import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import Col from 'react-bootstrap/lib/Col';
import Row from 'react-bootstrap/lib/Row';
import Grid from 'react-bootstrap/lib/Grid';
import Thumbnail from 'react-bootstrap/lib/Thumbnail';
import Navbar from 'react-bootstrap/lib/Navbar';
import FormGroup from 'react-bootstrap/lib/FormGroup';
import FormControl from 'react-bootstrap/lib/FormControl';
import Button from 'react-bootstrap/lib/Button';

class App extends Component {

    //constructor
    constructor(props){
        super(props);
        this.state = {
            imgs:[],
            isLoad: false,
        };
    }

    //overwrite componentDidMount
    componentDidMount() {
        this.loadPhoto();
    }

    //loading the photos
    loadPhoto(){
        axios.get('http://localhost:3002/')
            .then(response => {
                console.log(reposnse.data);
                this.setState({ imgs:response.data,isLoad:true});
            })
            .catch(error => {
                console.log('error here');
            })
    }

    //search photo
    searchPhoto(){
        console.log('hello here');
    }

    //render function
  render() {
    return (
      <div className="App">
          <div className="header">
              <Navbar>
                  <Navbar.Header>
                      <Navbar.Toggle />
                  </Navbar.Header>
                  <Navbar.Collapse>
                      <Navbar.Form pullLeft>
                          <FormGroup>
                              <FormControl type="text" placeholder="Search" />
                          </FormGroup>{' '}
                          <Button type="submit" onChange={this.searchPhoto()}>Submit</Button>
                      </Navbar.Form>
                  </Navbar.Collapse>
              </Navbar>
          </div>
          <div className="content">
              <Grid>
                  <Row>
                      {this.state.imgs.map((image) => (
                          <Col xs={6} md={4} key={image.id}>
                              <Thumbnail src={image.urls.small} alt="242x200">
                                  <h3>Photo By:</h3>
                                  <a href={image.user.links.html}>{image.user.name}</a>
                              </Thumbnail>
                          </Col>
                          ))}
                  </Row>
              </Grid>
          </div>
      </div>
    );
  }
}

export default App;
