import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import Image from 'react-bootstrap/lib/Image';
import Col from 'react-bootstrap/lib/Col';
import Row from 'react-bootstrap/lib/Row';
import Grid from 'react-bootstrap/lib/Grid';

class App extends Component {

    //constructor
    constructor(props){
        super(props);
        this.state = {
            imgs:[],
            isLoad: false,
        };
    }

    //overwrite componentDidMount to talk to backend
    componentDidMount() {
        axios.get('http://localhost:3002/')
            .then(response => {
            this.setState({ imgs:response.data,isLoad:true});
        })
        //     (function(response){
        //         this.setState({imgs:response.data,isLoad:true});
        // })
            .catch(function (error) {
                if (error.response) {
                    // The request was made and the server responded with a status code
                    // that falls out of the range of 2xx
                    console.log(error.response.data);
                    console.log(error.response.status);
                    console.log(error.response.headers);
                } else if (error.request) {
                    // The request was made but no response was received
                    // `error.request` is an instance of XMLHttpRequest in the browser and an instance of
                    // http.ClientRequest in node.js
                    console.log(error.request);
                } else {
                    // Something happened in setting up the request that triggered an Error
                    console.log('Error', error.message);
                }
                console.log(error.config);
            })
    }

    //render function
  render() {
    return (
      <div className="App">
          <div className="header">
              <div className="inner">
                  <h1 className="title">SearchBar</h1>
              </div>
          </div>
          <div className="content">
              <Grid>
                  <Row>
                      <Col xs={6} md={4}>
                          <Thumbnail src={image.urls.small} alt="242x200">
                              <h3>Thumbnail label</h3>
                              <p>Description</p>
                              <p>
                                  <Button bsStyle="primary">Button</Button>
                                  &nbsp;
                                  <Button bsStyle="default">Button</Button>
                              </p>
                          </Thumbnail>
                      </Col>



                      {this.state.imgs.map((image) => (
                          <Col xs={4} md={3}>
                              <div key={image.id}>
                                  <Image src={image.urls.small} responsive/>
                              </div>
                          </Col>
                          ))}
                  </Row>
              </Grid>
          </div>
      </div>
    );
  }
}

export default App;
